/**
 *
 * @export
 * @interface LtcUTXO
 */
export interface LtcUTXO {
    /**
     * Version of the UTXO.
     * @type {number}
     * @memberof LtcUTXO
     */
    version: number;
    /**
     *
     * @type {number}
     * @memberof LtcUTXO
     */
    height: number;
    /**
     * Amount of UTXO in satoshis.
     * @type {string}
     * @memberof LtcUTXO
     */
    value: string;
    /**
     * Data generated by a spender which is almost always used as variables to satisfy a pubkey script.
     * @type {string}
     * @memberof LtcUTXO
     */
    script: string;
    /**
     * Address of the owner of the UTXO.
     * @type {string}
     * @memberof LtcUTXO
     */
    address: string;
    /**
     * Coinbase transaction - miner fee.
     * @type {boolean}
     * @memberof LtcUTXO
     */
    coinbase: boolean;
    /**
     * Transaction hash.
     * @type {string}
     * @memberof LtcUTXO
     */
    hash: string;
    /**
     * Transaction index of the output.
     * @type {number}
     * @memberof LtcUTXO
     */
    index: number;
}
